import React, { useState, useRef, useEffect } from 'react';

export default function TerminalPortfolio() {
  const [output, setOutput] = useState([
    "Welcome to Irene's Portfolio! Type 'help' to see available commands."
  ]);
  const [currentCommand, setCurrentCommand] = useState('');
  const [commandHistory, setCommandHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const inputRef = useRef(null);
  const terminalRef = useRef(null);

  const commands = {
    help: [
      "Available commands:",
      "- about: Learn about Irene",
      "- projects: View project portfolio",
      "- skills: See technical skills",
      "- contact: Get contact information",
      "- clear: Clear the terminal"
    ],
    about: [
      "Irene Rivero Casal",
      "Role: System Administrator / DevOps Engineer",
      "Passionate about Linux, automation, and infrastructure."
    ],
    projects: [
      "1. Webserv - HTTP server in C++",
      "2. Minishell - Custom shell in C",
      "3. Inception - Docker & Virtualization setup"
    ],
    skills: [
      "Technical Skills:",
      "- Linux",
      "- Docker",
      "- Bash",
      "- Networking",
      "- MariaDB",
      "- Nginx"
    ],
    contact: [
      "Contact Information:",
      "Email: irene.rivero.casal@gmail.com",
      "GitHub: github.com/irrivero"
    ]
  };

  useEffect(() => {
    // Auto-scroll to bottom of terminal
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [output]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedCommand = currentCommand.trim().toLowerCase();

    // Add to output
    const newOutput = [...output, `$ ${currentCommand}`];

    // Process command
    if (trimmedCommand === 'clear') {
      setOutput(["Welcome to Irene's Portfolio! Type 'help' to see available commands."]);
    } else if (commands[trimmedCommand]) {
      setOutput([...newOutput, ...commands[trimmedCommand]]);
    } else {
      setOutput([...newOutput, `Command not found: ${trimmedCommand}`]);
    }

    // Update command history
    if (trimmedCommand) {
      setCommandHistory(prev => [...prev, trimmedCommand]);
    }

    // Reset command
    setCurrentCommand('');
    setHistoryIndex(-1);
  };

  const handleKeyDown = (e) => {
    // Handle arrow up/down for command history
    if (e.key === 'ArrowUp') {
      e.preventDefault();
      if (commandHistory.length > 0) {
        const newIndex = historyIndex < commandHistory.length - 1 
          ? historyIndex + 1 
          : commandHistory.length - 1;
        setCurrentCommand(commandHistory[commandHistory.length - 1 - newIndex]);
        setHistoryIndex(newIndex);
      }
    } else if (e.key === 'ArrowDown') {
      e.preventDefault();
      if (historyIndex > -1) {
        const newIndex = historyIndex - 1;
        if (newIndex >= 0) {
          setCurrentCommand(commandHistory[commandHistory.length - 1 - newIndex]);
        } else {
          setCurrentCommand('');
        }
        setHistoryIndex(newIndex);
      }
    }
  };

  return (
    <div 
      ref={terminalRef}
      className="bg-black text-green-500 p-4 font-mono text-sm h-screen overflow-auto"
    >
      {output.map((line, index) => (
        <div key={index}>{line}</div>
      ))}
      <form onSubmit={handleSubmit} className="flex">
        <span className="mr-2">$</span>
        <input
          ref={inputRef}
          type="text"
          value={currentCommand}
          onChange={(e) => setCurrentCommand(e.target.value)}
          onKeyDown={handleKeyDown}
          className="bg-transparent text-green-500 outline-none flex-grow"
          autoFocus
        />
      </form>
    </div>
  );
}
