import React, { useEffect, useRef, useState } from "react";
import { Terminal } from "xterm";
import "xterm/css/xterm.css";

export default function TerminalPortfolio() {
  const terminalRef = useRef(null);
  const termInstance = useRef(null);
  const [commandHistory, setCommandHistory] = useState([]);
  const [currentCommand, setCurrentCommand] = useState("");

  useEffect(() => {
    if (termInstance.current) return; // Prevent multiple terminal instances

    const xterm = new Terminal({
      cursorBlink: true,
      rows: 20,
      theme: { background: "#000", foreground: "#00ff00" },
    });

    xterm.open(terminalRef.current);
    xterm.writeln("Welcome to Irene's Portfolio! Type 'help' to see available commands.");
    xterm.write("$ ");
    termInstance.current = xterm;

    xterm.onKey(({ key, domEvent }) => handleInput(xterm, key, domEvent));
  }, []);

  const commands = {
    help: "Available commands: about, projects, skills, contact, clear",
    about: "Irene Rivero Casal - System Administrator / DevOps Engineer. Passionate about Linux, automation, and infrastructure.",
    projects: "1. Webserv - HTTP server in C++\n2. Minishell - Custom shell in C\n3. Inception - Docker & Virtualization setup",
    skills: "Linux, Docker, Bash, Networking, MariaDB, Nginx",
    contact: "Email: irene.rivero.casal@gmail.com\nGitHub: github.com/irrivero",
    clear: "clear",
  };

  const handleInput = (xterm, key, domEvent) => {
    if (domEvent.key === "Enter") {
      const command = currentCommand.trim().toLowerCase(); // Normalize to lowercase
      xterm.writeln("");

      if (commands.hasOwnProperty(command)) {
        const output = commands[command];
        if (output === "clear") {
          xterm.clear();
        } else {
          xterm.writeln(output);
        }
      } else {
        xterm.writeln(`Command not found: ${command}`);
      }

      setCommandHistory([...commandHistory, command]);
      setCurrentCommand("");
      xterm.write("$ ");
    } else if (domEvent.key === "Backspace") {
      if (currentCommand.length > 0) {
        setCurrentCommand(currentCommand.slice(0, -1));
        xterm.write("\b \b");
      }
    } else {
      setCurrentCommand(currentCommand + key);
      xterm.write(key);c
    }
  };

  return <div ref={terminalRef} style={{ width: "100%", height: "100vh" }}></div>;
}
